<template>
  <div class="card">
    <div class="left">
      <div>{{ workData.baseMap?.depname }}</div>
      <div>
        <p>{{ workData.baseMap?.hosname }}</p>
        <p>{{ workData.baseMap?.bigname }}</p>
      </div>
    </div>
    <div class="right">
      <div class="focus">关注</div>
    </div>
  </div>
  <div class="floor">
    <div class="scroll">
      <div class="date">
        <div
          class="date-card"
          v-for="item in workData.bookingScheduleList"
          :key="item.id"
          :class="{
            activenone:
              (item.status == -1 || item.availableNumber == -1) &&
              item.workDate == workTime.workDate,
            activehas:
              item.status !== -1 &&
              item.availableNumber !== -1 &&
              item.workDate == workTime.workDate,
            activequick: item.status == 1 && item.workDate == workTime.workDate,
          }"
          @click="changeSelect(item)">
          <div>{{ item.dayOfWeek }}</div>
          <div>{{ item.workDateMd }}</div>
          <div v-if="item.status == -1" class="wuhao">约满</div>
          <div
            v-if="item.status == 0"
            :class="item.availableNumber == -1 ? 'wuhao' : 'youhao'">
            {{ item.availableNumber == -1 ? "无号" : `有号` }}
          </div>

          <div v-if="item.status == 1" class="quick">即将放号</div>
        </div>
      </div>
    </div>
